import React, { useState, useEffect } from 'react';
import './App.css';
import FetchTodos from './components/FetchTodos';
import Form from './components/Form';
import TodoList from './components/TodoList';
import ApiTodoList from './components/ApiTodoList';

function App() {

  const [inputText, setInputText] = useState('');
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState("all")
  const [filteredTodos, setFilteredTodos] = useState([])
  const [apiTodos, setApiTodos] = useState([]);
  const [apiStatus, setApiStatus] = useState("all")
  const [apiFilteredTodos, setApiFilteredTodos] = useState([])

  useEffect(() => {
    filterHandler()
  }, [todos, status])

  useEffect(() => {
    apiFilterHandler()
  }, [apiTodos, apiStatus])

  const filterHandler = () => {
    switch(status){
      case 'completed':
        setFilteredTodos(todos.filter(todo => todo.completed === true))
        break
      case 'uncompleted':
        setFilteredTodos(todos.filter(todo => todo.completed === false))
        break
      default: 
        setFilteredTodos(todos)
        break
    }
  }
  const apiFilterHandler = () => {
    switch(apiStatus){
      case 'completed':
        setApiFilteredTodos(apiTodos.filter(apiTodo => todo.completed === true ))
        break
      case 'uncompleted':
        setApiFilteredTodos(apiTodos.filter(apiTodo => apiTodo.completed === false))
        break
      default:
        setApiFilteredTodos(apiTodos)
        break
    }
  }

  return (
    <div className="App">
      <header>
        <h1>To-Do List</h1>    
      </header>
      <Form 
        todos={todos}
        inputText={inputText}
        setTodos={setTodos} 
        setInputText={setInputText}
        setStatus={setStatus} 
        />
      <TodoList 
        filteredTodos={filteredTodos}
        setTodos={setTodos} 
        todos={todos}
      />
      <FetchTodos />
      <ApiTodoList 
        apiFilteredTodos={apiFilteredTodos}
        setApiTodos={setApiTodos} 
        apiTodos={apiTodos}
      />
    </div>
  );
}

export default App;
