import React, { Component } from 'react';

class FetchTodos extends Component {
  state = {
    todo: '',
  }

  componentDidMount() {
    this.fetchTodo();
  }

  fetchTodo = () => {
    axios.get('https://api.adviceslip.com/advice')
      .then((response) => {
        const { todo } = response.data.slip;

        this.setState({ todo });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <button>Fetch Todos</button>
      </div>
    )
  }
}

export default FetchTodos
