  useEffect( () => {
    axios.get('https://jsonplaceholder.typicode.com/todos')
    .then((res) => {
      const todos = res.data
      console.log(todos)
      setTodos(todos);
    })
  }, [])

  const deleteHandler = (e) => {
    const name = e.target.value
    updateList(Todos.filter(item => item.name !== name)); 
    console.log(name)

  }

  const updateList = (e) => {
    setTodos(e)

  }

  return (
    <div>
        <ul className='api-todos'>{
          Todos.map(Todo => <li key={Todos.id} name = {Todos.id}> <button className='trashbtn' onClick={deleteHandler}><i className='fas fa-trash'>{ Todo.title} </i></button></li>)
        }</ul>
    </div>
  )
}

export default FetchTodos
